(1, 'WebLoginSidebar', 'WebLogin Tpl', 0, 1, 0, '\n<!-- #declare:separator <hr> --> \n<!-- login form section-->\n<form method="post" name="loginfrm" action="[+action+]" style="margin: 0px; padding: 0px;"> \n<input type="hidden" value="[+rememberme+]" name="rememberme"> \n<table border="0" cellspacing="0" cellpadding="0">\n<tr>\n<td>\n<table border="0" cellspacing="0" cellpadding="0">\n  <tr>\n	<td><b>User:</b></td>\n	<td><input type="text" name="username" tabindex="1" onkeypress="return webLoginEnter(document.loginfrm.password);" size="5" style="width: 100px;" value="[+username+]" /></td>\n  </tr>\n  <tr>\n	<td><b>Password:</b></td>\n	<td><input type="password" name="password" tabindex="2" onkeypress="return webLoginEnter(document.loginfrm.cmdweblogin);" size="5" style="width: 100px;" value="" /></td>\n  </tr>\n  <tr>\n	<td><label for="chkbox" style="cursor:pointer">Remember me:&nbsp; </label></td>\n	<td>\n	<table width="100%"  border="0" cellspacing="0" cellpadding="0">\n	  <tr>\n		<td valign="top"><input type="checkbox" id="chkbox" name="chkbox" tabindex="4" size="1" value="" [+checkbox+] onClick="webLoginCheckRemember()" /></td>\n		<td align="right">									\n		<input type="submit" value="[+logintext+]" name="cmdweblogin" /></td>\n	  </tr>\n	</table>\n	</td>\n  </tr>\n  <tr>\n	<td colspan="2"><a href="#" onclick="webLoginShowForm(2);return false;">Forget Password?</a></td>\n  </tr>\n</table>\n</td>\n</tr>\n</table>\n</form>\n<hr>\n<!-- log out hyperlink section -->\n<a href=''[+action+]''>[+logouttext+]</a>\n<hr>\n<!-- Password reminder form section -->\n<form name="loginreminder" method="post" action="[+action+]" style="margin: 0px; padding: 0px;">\n<input type="hidden" name="txtpwdrem" value="0" />\n<table border="0">\n	<tr>\n	  <td>Enter the email address of your account <br />below to receive your password:</td>\n	</tr>\n	<tr>\n	  <td><input type="text" name="txtwebemail" size="24" /></td>\n	</tr>\n	<tr>\n	  <td align="right"><input type="submit" value="Submit" name="cmdweblogin" />\n	  <input type="reset" value="Cancel" name="cmdcancel" onclick="webLoginShowForm(1);" /></td>\n	</tr>\n  </table>\n</form>\n\n', 0),
(2, 'mm_demo_rules', 'ManagerManager rules for the demo content. Should be modified for your own sites.', 0, 2, 0, '\n\n// PHP *is* allowed\n// $news_role and $news_tpl will not apply to demo content but are left as a demonstration of what can be done\n\n// For everyone\nmm_default(''pub_date'');\nmm_renameField(''introtext'',''Summary'');\nmm_changeFieldHelp(''alias'', ''The URL that will be used to reach this resource. Only numbers, letters and hyphens can be used'');\nmm_widget_tags(''documentTags'','' ''); // Give blog tag editing capabilities to the ''documentTags (3)'' TV\nmm_widget_showimagetvs(); // Always give a preview of Image TVs\n// mm_widget_colors(''color'', ''#666666''); // make a color selector widget for the ''colour'' TV\n\n// For everyone except administrators\nmm_hideFields(''link_attributes'', ''!1'');\nmm_hideFields(''loginName '', ''!1'');\n// mm_renameField(''alias'',''URL alias'',''!1'');\n\n// News editors role -- creating a variable makes it easier to manage if this changes in the future\n$news_role = ''3'';\nmm_hideFields(''pagetitle,menutitle,link_attributes,template,menuindex,description,show_in_menu,which_editor,is_folder,is_richtext,log,searchable,cacheable,clear_cache'', $news_role);\nmm_renameTab(''settings'', ''Publication settings'', $news_role);	\nmm_synch_fields(''pagetitle,menutitle,longtitle'', $news_role);\nmm_renameField(''longtitle'',''Headline'', $news_role, '''', ''This will be displayed at the top of each page'');\n\n// News story template\n$news_tpl = ''8'';\n// mm_createTab(''Categories'',''HrCats'', '''', $news_tpl, '''', ''600'');\n// mm_moveFieldsToTab(''updateImage1'', ''general'', '''', $news_tpl);\n// mm_hideFields(''menuindex,show_in_menu'', '''', $news_tpl);\nmm_changeFieldHelp(''longtitle'', ''The story\\''s headline'', '''', $news_tpl);\nmm_changeFieldHelp(''introtext'', ''A short summary of the story'', '''', $news_tpl);\nmm_changeFieldHelp(''parent'', ''To move this story to a different folder: Click this icon to activate, then choose a new folder in the tree on the left.'', '''', $news_tpl);\n\n', 0),
(3, 'agricultural', 'Agricultural Copy', 0, 7, 0, '<div id=''agricultural''>\r\n<p><strong>Agricultural</strong></p>\r\n<p>\r\n<img src=''assets/images/KCOF coffee rows small.png'' alt=''KCOF Coffee Rows'' style=''float:right; height:210px; padding: 0 0 0 10px;'' />\r\nFrom many years of working with farmers and scientists, we have\r\nexperience that encompasses every stage of coffee production from the nursery\r\nto green coffee processing.&nbsp; We can\r\naid in creating or improving your perfect production system.</p>\r\n<ul>\r\n<li>Field planning</li>\r\n<li>Field maintenance</li>\r\n<li>Soil and leaf sampling</li>\r\n<li>Processing</li>\r\n<li>Quality control</li>\r\n<li>Development of sustainable systems</li>\r\n</ul>\r\n</div>\r\n\r\n\r\n\r\n\r\n', 0),
(4, 'quality', 'Quality Copy', 0, 7, 0, '<div id=''quality''>\r\n<p><strong>Quality Assessment</strong></p>\r\n<p>\r\n<img src=''assets/images/ss cupping at Downtown Coffee 10-6-08.jpg'' alt=''Cupping'' style=''float:right;  height: 20em; padding: 0 0 0 10px;'' />\r\n\r\nTo many consumers, the most important aspect of coffee is its sensory\r\nproperties.&nbsp; Thoroughly\r\nunderstanding your coffee from seed to cup allows you to provide them exactly\r\nwhat they want.&nbsp; Coffea Consulting\r\nis eager to help you learn about your coffee''s quality and, if necessary, help\r\nimprove it.  With a licensed Q-grader available at Coffea Consulting, you are assured an evaluation that meets international standards. \r\n<img src=''assets/images/Cupping Medium.jpg'' alt=''Cupping Table'' style=''float:right;  height: 182px; margin: 10px 0 0 0; padding: 0 5px 0 5px;'' />\r\n</p>\r\n<ul>\r\n<li>Green coffee</li>\r\n<li>Roasted coffee</li>\r\n<li>Brewed coffee, including espresso</li>\r\n</ul>\r\n</div>\r\n\r\n', 0),
(5, 'educational', 'Educational Copy', 0, 7, 0, '<div id=''educational''>\r\n<p><strong>Educational</strong></p>\r\n<p>Coffee is a fascinating beverage that resonates with people on many\r\ndifferent levels.&nbsp; We have\r\nextensive experience giving talks and workshops on coffee-related topics and\r\nhave published a book that\r\ncontains information about every aspect of coffee.</p>\r\n<p>\r\n\r\nWe are available for speaking engagements, workshops, and freelance\r\nwriting. &nbsp;If you, your business, or\r\njust a group of friends would like to learn more about coffee, Coffea\r\nConsulting will design an educational experience that best fits your needs.&nbsp; </p>\r\n<p>\r\n<img src=''assets/images/COH cupping Medium.jpg'' alt=''Cupping'' style=''float:right; height: 140px; padding: 0 0 0 10px;'' />\r\n<img src=''assets/images/kau workshop small.jpg'' alt=''Workshop'' style=''float:right; height: 140px; padding: 0 0 0 10px;'' />\r\nSome potential topics:\r\n</p>\r\n<ul>\r\n<li>Production</li>\r\n<li>Roasting</li>\r\n<li>Brewing</li>\r\n<li>Cupping</li>\r\n<li>Quality</li>\r\n<li>Ethical coffees</li>\r\n</ul>\r\n</div>\r\n', 0),
(8, 'about-us', '', 0, 7, 0, '<p>\r\n<img src=''assets/images/Diane and ss 9-9-09.jpg'' alt=''TV Appearance'' style=''float: right; height: 145px; padding: 0 0 0 15px;'' />\r\nCoffea Consulting is\r\nowned and operated by Shawn Steiman.&nbsp;\r\nShawn holds MS and PhD degrees from the University of Hawai&lsquo;i in the\r\nfield of Tropical Plant and Soil Sciences. You can&nbsp;view Shawn&rsquo;s CV <a href="[~8~]" target="_blank">here</a>. He has researched various aspects of coffee science,\r\nincluding horticulture, biochemistry, entomology, brewing and sensory analysis.&nbsp; He is also a licensed Q-grader.   </p>\r\nHis passion and love of coffee inspired\r\nhim to write <em><a href="[~5~]">The Hawai&lsquo;i Coffee Book: A\r\nGourmet''s Guide from Kona to Kaua&lsquo;i</a></em>.&nbsp;\r\nThis book offers a comprehensive guide to coffee production and\r\nconsumption while detailing the intricacies of the Hawai&lsquo;i coffee\r\nindustry.&nbsp;\r\nHe also co-edited and wrote some chapters in <em><a href="[~5~]">Coffee: A Comprehensive Guide to the Bean, the Beverage, and the Industry.  </a></em>The book covers an array of coffee topics, devoting entire chapters to single ideas.\r\n<p>&nbsp;</p>\r\n\r\n<div style="padding: 1px; position: absolute; top: 179px; right: 10px;">\r\nProud member since 2008<br />\r\n<img src="assets/images/SCAA logo.png" alt="Specialty Coffee Association" />\r\n</div>\r\n<div style="padding: 1px; position: absolute; top: 179px; left: 41px;">\r\n</div>', 0),
(9, 'media', 'Media appearances Copy', 0, 7, 0, '<p>\r\n<ul>\r\n<li>Coffee; Hawai&lsquo;i Public Radio''s "Business Beyond the Reef," May 13, 2008. <br />\r\n<a href=''assets/media/Coffee-HPR 5-13-08.mp3'' target=''_blank''>HPR episode</a><br /></li>\r\n<li>Coffee Lovers Bypass Coffee Shops and Brew at Home.  Obra, Joan.  <i>The Fresno Bee</i>.  9 June 2009.<br />\r\n<li>Hawai&lsquo;i Cupping Aims to Showcase Coffee Beyond Kona.  Leif, Dan.  <i>Fresh Cup Magazine</i>, online article. 9 July 2009.<br />  \r\n<a href=''http://www.freshcup.com/news_events_details.php?id=64'' target=''_blank''>Hawai&lsquo;i cupping aims to showcase coffee beyond Kona</a><br /></li>\r\n<li>Live at the Hawai&lsquo;i State Farm Fair. Hawai&lsquo;i-arukikata web magazine, 19 July 2009.<br />\r\n<a href=''http://web2.hawaii-arukikata.com/tabid/56/EntryId/57/.aspx'' target=''_blank''>Live at the Hawai&lsquo;i State Farm Fair</a><br /></li>\r\n<li>Hawaii&lsquo;s Coffee Doctor to Give Education Talks.  Berusch, Brian.  B on Hawai&lsquo;i, Nov 3, 2009.<br />\r\n<a href=''http://www.bonhawaii.com/hawaiis-coffee-doctor-give-educational-talks-kona'' target=''_blank''>Hawaii&lsquo;s doffee doctor to give education talks</a><br /></li>\r\n<li>Beyond Kona Coffee; Wu, Nina.  <i>The Honolulu Star Bulletin</i>, November 22, 2009. <br />\r\n<li>Taste of Paradise: Kona in a Cup; Paiva, Derek.  <i>Hawai&lsquo;i Magazine</i>, January/February 2010, Vol 27, Issue 1. <br />\r\n<a href=''http://www.hawaiimagazine.com/blogs/hawaii_today/2010/3/22/Kona_coffee_Hawaii_closer_look'' target=''_blank''>Taste of paradise: Kona in a cup</a></li>\r\n<li>Barista wins first place with Hawaii-grown coffee; Engle, Erika.  <i>The Honolulu Star Bulletin</i>, March 4, 2010.\r\n<li>Blenders Refute Coffee Study; Jensen, Chelsea.  <i>West Hawaii Today</i>, March 10, 2010. <br /><a href=''http://www.westhawaiitoday.com/articles/2010/03/10/local/local04.txt'' target=''_blank''>Blenders refute coffee study</a></li>\r\n<li>Farm to Table: Coffee; Leong, Lavonne.  <i>Honolulu Magazine</i>, July 2010.<br /><a href=''assets/docs/Farm to Table Coffee - Hnl Magazine Leong - 2010.PDF'' target=''_blank''>Farm to table: Coffee</a></li>\r\n<li>A World Class Brew; Oshiro, Joleen. <i>Honolulu Star-Advertiser</i>, July14, 2010.<br /><a href=''http://www.staradvertiser.com/features/featuresstories/20100714_A_world-class_brew.html''>A world-class brew</a></li>\r\n</ul>\r\n</p>\r\n', 0),
(6, 'research', 'Research Copy', 0, 7, 0, '<div id=''research''>\r\n<p><strong>Research</strong></p>\r\n<p>\r\n<img src=''assets/images/lab small.jpg'' alt=''Lab'' style=''float:right; height: 230px; padding: 0 0 0 10px;'' />\r\nOur expertise is rooted in scientific research.&nbsp; We continue to\r\nexplore and learn about coffee through experimentation, attending conferences\r\nand reviewing the latest scientific articles and other publications.&nbsp;\r\nCoffea Consulting is able to consolidate pre-existing literature for you in an\r\naccessible, easy to read report.&nbsp;&nbsp;We will also design and execute\r\nexperiments to help you find the answers to your questions.<br />\r\n<img src=''assets/images/shade small.jpg'' alt=''Shade Coffee'' style=''float: right; height: 149px; padding: 0 65px 0 10px;'' />\r\n<img src=''assets/images/Caffeine molecule.png'' alt=''Caffeine'' style=''float: right; height: 145px; padding: 0 65px 0 10px;'' />\r\n</p>\r\n</div>\r\n', 0),
(7, 'tastings', 'Tasting and Cuppings Copy', 0, 7, 0, '<div id=''tastings''>\r\n<p><strong>Private Tastings and Cuppings</strong></p>\r\n<p>\r\n<img src=''assets/images/profit medium.jpg'' alt=''Cupping'' style=''float: right; height: 230px; padding: 0 65px 0 10px;'' />\r\nCoffee is a drink worth thinking about. It can have unexpected, titillating flavors and aromas that\r\nchallenge our very expectation of what coffee should be.&nbsp; We believe everyone should appreciate\r\nthe depth of the experience one can have while drinking coffee.</p>\r\n<p>Coffea Consulting is proud to offer private tastings to help you, your\r\nfriends, your business, or your customers discover coffee''s potential. We will gladly come to your home or\r\nbusiness and explore the diverse world of coffee with you.</p>\r\n<p>Like wine, coffee has a formal method of evaluating quality, called\r\n"cupping." Cupping allows us to more objectively explore and\r\nunderstand coffee''s complexity. Coffea Consulting offers private cupping\r\nsessions in your home or business.&nbsp;\r\nWe will teach you the theory and practice of cupping and give you the\r\nskills necessary to pursue it on your own.</p>\r\n</div>', 0),
(10, 'selected-literature', 'Selected Literature Copy', 0, 7, 0, '<p>\r\n<ul>\r\n<li><b>Steiman, Shawn</b>. The Glory of Coffee.  The Independent Voice. April 2009,<br /><a href=''http://www.konacoffeefarmers.org/docs/newsletter-0409.pdf'' target=''_blank''>The glory of coffee</a></li>\r\n<li><b>Steiman, Shawn</b>. Why Assess Coffee Quality.  The Independent Voice. May 2009, <br /><a href=''http://www.konacoffeefarmers.org/docs/newsletter-0509.pdf'' target=''_blank''>Why assess coffee quality</a></li>\r\n<li><b>Steiman, Shawn</b>. How to Assess Coffee Quality.  The Independent Voice. June 2009, <br /><a href=''http://www.konacoffeefarmers.org/docs/newsletter-0609.pdf'' target=''_blank''>How to assess coffee quality</a></li>\r\n<li><b>Steiman, Shawn</b>. Hawaii&lsquo;s Top Coffees Slug It Out at Inaugural Competition.  <i>Fresh Cup Magazine</i>, September 2009. <br /> <a href=''http://www.freshcup.com/issue.php?id=84'' target=''_blank''> Fresh Cup Magazine</a></li>\r\n<li><b>Steiman, Shawn</b>. Hawaiian Coffees Shine in Statewide Cupping Competition.  <i>Barista Magazine</i>. August/September 2009.  <br /><a href=''http://baristamagazine.epubxpress.com/'' target=''_blank''>Barista Magazine</a></li>\r\n<li>Smith, V.E., <b>S. Steiman</b>, and C.R. Elevitch. 2009. Farm and Forestry Production and Marketing Profile for Coffee (<i>Coffea arabica</i>). In: Elevitch, C.R. (ed.). Specialty Crops for Pacific Island Agroforestry. Permanent Agriculture Resources (PAR), Holualoa, Hawai&lsquo;i.<br /><a href=''http://agroforestry.net/scps/Coffee_specialty_crop.pdf'' target=''_blank''>Profile for coffee</a></li>\r\n<li><b>Steiman, Shawn</b>. Experiencing the coffees of Hawaii.  Caf&#233 Europa, No. 39, April 2010.<br />\r\n<a href=''assets/docs/Cafe Europa 39 v20 4-2010.pdf'' target=''_blank''>Caf&#233 Europa, April 2010</a><br />\r\n<li><b>Steiman, Shawn</b>. For coffee evaluation, scores don''t add up.  Roast Magazine, July/August 2010.<br /><a href=''assets/docs/The Points Pinciple - Steiman and Davids - 2010.PDF'' target=''_blank''>The Points Principle</a></li>\r\n\r\n</ul>\r\n</p>\r\n', 0),
(12, 'sourcing', 'Sourcing copy', 0, 7, 0, '<p>Coffea Consulting has extensive knowledge of Hawaiian coffees&mdash;both green and roasted.  If you are a roaster, restaurant, or business seeking Hawaii coffee, we can help you source that coffee.</p>', 0),
(13, 'clients', 'Client list page', 0, 7, 0, '<p>Clients include:</p>\r\n\r\n<table><tr><td style=''vertical-align:top''>\r\n\r\n<ul>\r\n<b>Agricultural</b>\r\n<li>Braund Farms</li>\r\n<li>Kaanapali Coffee Farms  <a target=''_blank'' href=''http://www.kaanapalicoffeefarms.com/''>Link</a></li>\r\n<li>Kona Earth <a target=''_blank'' href=''http://www.konaearth.com/''>Link</a></li>\r\n<li>Maui Jo Coffee Co. <a target=''_blank'' href=''http://mauijo.blogspot.com/''>Link</a></li>\r\n<li>Papa Kona Coffee Farm <a target=''_blank'' href=''http://papakona.com/''>Link</a></li><br />\r\n<b>Quality Assessment</b>\r\n<li>The Coffee Store  <a target=''_blank'' href=''http://www.mauicoffee.com/''>Link</a></li>\r\n<li>Coffees of Hawaii  <a target=''_blank'' href=''http://www.coffeesofhawaii.com/''>Link</a></li>\r\n<li>Kona View Estate <a target=''_blank'' href=''http://konaviewestate.com/''>Link</a></li>\r\n<li>Makahiki Farms <a target=''_blank'' href=''http://makahikifarms.com/''>Link</a></li>\r\n<li>Wailapa Farms  <a target=''_blank'' href=''http://www.wailapafarms.com/''>Link</a></li><br />\r\n<b>Research</b>\r\n<li>Specialty Coffee Association of America  <a target=''_blank'' href=''http://scaa.org/''>Link</a></li><br />\r\n\r\n</ul>\r\n</td>\r\n<td  style=''vertical-align:top''>\r\n<ul>\r\n<b>Educational</b>\r\n<li>Hawaii Farm Bureau Federation  <a target=''_blank'' href=''http://www.hfbf.org/''>Link</a></li>\r\n<li>ING Direct <a target=''_blank'' href=''http://home.ingdirect.com/''>Link</a></li>\r\n<li>Island X Hawaii  <a target=''_blank'' href=''http://islandxhawaii.com/''>Link</a></li>\r\n<li>Kona Coffee Cultural Festival <a target=''_blank'' href=''http://www.konacoffeefest.com/''>Link</a></li>\r\n<li>Department of Agriculture, Puerto Rico  <a target=''_blank'' href=''http://www.agricultura.gobierno.pr/''>Link</a></li><br />\r\n<b>Private Tastings & Cuppings</b>\r\n<li>Cades Schutte LLP  <a target=''_blank'' href=''http://www.cades.com/''>Link</a></li>\r\n<li>Cake Works  <a target=''_blank'' href=''http://cakeworkshi.com/''>Link</a></li>\r\n<li>Profit Point  <a target=''_blank'' href=''http://www.profitpt.com/''>Link</a></li>\r\n<li>Schoonmaker LLC </li>\r\n<li>Slow Food Oahu  <a target=''_blank'' href=''http://www.slowfoodoahu.org/''>Link</a></li><br />\r\n<b>Sourcing</b>\r\n<li>Beanetics <a target=''_blank'' href=''http://www.beanetics.com/''>Link</a></li>\r\n<li>Beach Bum Cafe <a target=''_blank'' href=''http://beachbumcafe.com/''>Link</a></li><br />\r\n</ul>\r\n\r\n</td></tr></table>\r\n', 0),
(14, 'media-2', 'Media 2 copy', 0, 7, 0, '<p>\r\n<ul>\r\n<li>Adventures in Coffee Land; Katz, Jesse.  <i>Hana Hou Magazine.</i>Vol 13, Issue 5, October/November, 2010. P 56-69. <br /><a href=''http://www.hanahou.com/pages/Magazine.asp?Action=DrawArticle&ArticleID=907&MagazineID=58''>Adventures in Coffee Land</a></li>\r\n<li>Buddah''s Cup tops at Kona Coffee Cupping Competition; Stanton, Karin. <i>Hawaii 24/7</i>, November 12, 2010.<br /><a href=''http://www.hawaii247.com/2010/11/12/buddhas-cup-tops-coffee-at-kona-coffee-cupping-competition/''>2010 Gevalia Kona Coffee Cupping Competition</a></li>\r\n<li>Coffee Beetle Leaves State Seeking Quick Fix; Levine, Michael. <i>Honolulu Civil Beat</i>, November 16, 2010.<br /><a href=''http://www.civilbeat.com/articles/2010/11/16/6538-coffee-beetle-leaves-state-seeking-quick-fix/''>Coffee Beetle in Hawaii</a></li>\r\n<li>What''s brewing? Seeking science in the cup; Bushman, Tavaner.  <i>Medill Reports.</i>, December 3, 2010.<br /><a href=''http://news.medill.northwestern.edu/chicago/news.aspx?id=174890''>Seeking science</a></li>\r\n<li>Cat Chat 14: Coffee Geeks; Toth, Catherine.<i>NonStop Honolulu.</i>, February 9, 2011.<br /><a href=''http://www.nonstophonolulu.com/blogs/thedailydish/cat-chat-14-coffee-geeks/''>Coffee Geeks</a></li>\r\n<li>The Best Part of Waking Up; Harmon, Anna.<i>The Honolulu Weekly.</i>, June 8, 2011.<br /><a href=''http://honoluluweekly.com/restaurants/2011/06/the-best-part-of-waking-up//''>The Weekly interview of Shawn Steiman</a></li>\r\n<li>A new spot downtown bills itself as a "microbrew" cafe with Hawaii coffees; Oshiro, Joleen.<i>The Star-Advertiser.</i>, July 13, 2011.<br /><a href=''http://www.staradvertiser.com/features/featuresstories/20110712_Island_brews.html''>Beach Bum Cafe in Honolulu</a></li>\r\n</ul>\r\n</p> 	    \r\n', 0),
(15, 'selected-literature-2', 'Selected literature 2 Copy', 0, 7, 0, '<p>\r\n<ul>\r\n<li><b>Steiman, S,</b> Idol, T. and H. C. Bittenbender.  2007.  Analysis of kaolin particle film usage and its application on coffee.  HortScience. 42(7):1605-1608.<br />\r\n<a href=''assets/docs/Analysis of kaolin particle film use and its application on coffee - Steiman et al - 2007.pdf'' target=''_blank''>Surround WP/kaolin and coffee</a><br />\r\n<li><b>Steiman, S.</b>, Idol, T., Bittenbender, H.C., and L. Gautz..  2011.  Shade coffee in Hawai&lsquo;i - Exploring some aspects of quality, growth, yield, and nutrition.  Scientia Horticulturae.  128(2): 152-158.<br /><a href=''http://dx.doi.org/10.1016/j.scienta.2011.01.011''>Shade coffee in Hawai&lsquo;i</a><br />\r\n<li><b>Steiman, S</b>.  2011.  Handling the cherry: The risks and rewards of processing cherries.  Origin case Study: Hawaii.  Roast Magazine.  July/August.<br /><a href=''assets/docs/Handling the cherry- The risks and rewards of processing methods - Steiman - 2011.pdf'' target=''_blank''>Cherry processing with Hawai&lsquo;i as a case study</a><br />\r\n<li>Rodrigues, C., Brunner, M., <b>Steiman, S.</b>, Bowen, G., Nogueira, J., Gautz, L., Prohaska, T., and C. M&aacute;guas.  2011.  Isotopes as tracers of the Hawaiian coffee-producing regions.  Journal of Agricultural and Food Chemistry. 59(18): 10239-10246. <br /><a href=''http://dx.doi.org/10.1021/jf200788p''>Use of isotopes to discriminate Hawai&lsquo;i coffee origins</a><br />\r\n<li><b>Steiman, S</b>.  2011.  Hawaiian Coffee Primer.  Barista Magazine.  Dec/Jan. 39-43.<br /><a href=''assets/docs/Hawaiian coffee primer - Steiman - 2011.pdf'' target=''_blank''>An overview of the current Hawai&lsquo;i coffee industry</a><br />\r\n<li><b>Steiman, S</b>.  2012.  Taste the Rainbow State.  Fresh Cup Magazine.  Jan. 29-33.<br /><a href=''assets/docs/Taste of the Rainbow State - Steiman - 2012.pdf'' target=''_blank''>The current coffee scene in Hawai&lsquo;i</a><br />\r\n</ul>\r\n</p>\r\n\r\n', 0),
